public function multiplyByHundred(value:int):int {
  value = (value * 100);
  return value;
}

public function multiplyByHundred(value:int, reallyDoIt:Boolean):int {
  if (reallyDoIt) {
    value = value * 100;
  } else {
    value = value * 1000;
  }
  return value;
}

